import React, { useEffect , useState } from 'react';
import { View, FlatList, Text, TouchableOpacity, StyleSheet } from 'react-native';
import { listaHorarios } from '../api';
const ListHorarios = ({ route  , navigation}) => {

  const [horarios, setHorarios] = useState([]);

  useEffect(() => {
    const obtenerHorarios = async () => {
      try {
        const response = await listaHorarios();
        // Suponiendo que la respuesta de la API es un arreglo de objetos similar a 'data'
        setHorarios(response);
      } catch (error) {
        console.error('Error al obtener los horarios:', error);
      }
    };

    obtenerHorarios();
  }, []);
  

  const data = [
    { id: '1', nombre: 'Elemento 1', atributo1: 'A1', atributo2: 'B1', atributo3: 'C1' },
    { id: '2', nombre: 'Elemento 2', atributo1: 'A2', atributo2: 'B2', atributo3: 'C2' },
    { id: '3', nombre: 'Elemento 3', atributo1: 'A3', atributo2: 'B3', atributo3: 'C3' },
    // Agrega más elementos según sea necesario
  ];

  // Función para renderizar cada elemento de la lista
  const renderItem = ({ item }) => (
    <View style={styles.itemContainer}>
      <View style={styles.itemContent}>
        <Text style={styles.itemText}>{item.nombre}</Text>
        <Text style={styles.itemText}>{item.atributo1}</Text>
        <Text style={styles.itemText}>{item.atributo2}</Text>
        <Text style={styles.itemText}>{item.atributo3}</Text>
      </View>
      <TouchableOpacity style={styles.button}>
        <Text style={styles.buttonText}>Botón</Text>
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={horarios}
        keyExtractor={(item) => item.id}
        renderItem={renderItem}
      />
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  itemContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
    paddingVertical: 10,
  },
  itemContent: {
    flex: 1,
  },
  itemText: {
    fontSize: 16,
    marginBottom: 4,
  },
  button: {
    backgroundColor: '#3498db',
    padding: 10,
    borderRadius: 5,
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
  },
});
export default ListHorarios;
