import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Image,
  StyleSheet,
} from 'react-native';
import authService from '../services/authService';
import { useNavigation } from "@react-navigation/native";

export function RegisterView() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigation = useNavigation(); // Asignar useNavigation a la variable 'navigation'

    const handleRegister = async () => {
        try {
            if (!username || !password) {
                // Validación básica de campos no vacíos
                console.error('Por favor, complete todos los campos.');
                return;
            }

            const data = await authService.register(username, password);
            // Manejo de la respuesta del servidor después del registro exitoso
            console.log('Usuario registrado:', data);

            // Redirigir a la vista de inicio de sesión después del registro exitoso
            navigation.navigate('LoginView');
        } catch (error) {
            console.error('Error al registrar:', error.message);
            // Lógica para manejar errores, por ejemplo, mostrar un mensaje al usuario
        }
    };

    return (
        <View style={styles.container}>
            {/* Logo */}
            <Image
                source={{
                    uri:
                        'https://thumbs.dreamstime.com/b/hand-pointing-qr-code-vector-blue-concept-icon-symbol-hand-pointing-qr-code-vector-blue-concept-icon-symbol-thin-line-109008386.jpg',
                }}
                style={styles.logo}
            />

            {/* Resto del contenido */}
            <Text style={styles.title}>Registrarse</Text>
            <View style={styles.inputContainer}>
                <TextInput
                    style={styles.input}
                    placeholder="Correo electrónico"
                    onChangeText={setUsername}
                    value={username}
                />
            </View>
            <View style={styles.inputContainer}>
                <TextInput
                    style={styles.input}
                    placeholder="Contraseña"
                    onChangeText={setPassword}
                    value={password}
                    secureTextEntry
                />
            </View>
            <TouchableOpacity style={styles.button} onPress={handleRegister}>
                <Text style={styles.buttonText}>Registrarse</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.loginButton} onPress={() => navigation.navigate('LoginView')}>
                <Text style={styles.loginText}>¿Ya tienes cuenta? Inicia sesión</Text>
            </TouchableOpacity>

        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        padding: 16,
        backgroundColor: "#2B2D42",
    },
    logo: {
        width: 150,
        height: 150,
        marginBottom: 20,
        resizeMode: "contain", // Ajustar al contenido del tamaño definido
    },
    title: {
        fontSize: 24,
        fontWeight: "bold",
        color: "#8D99AE",
        marginBottom: 16,
    },
    inputContainer: {
        marginBottom: 16,
        width: "100%",
    },
    input: {
        backgroundColor: "#EDF2F4",
        padding: 10,
        borderRadius: 8,
        fontSize: 16,
    },
    button: {
        backgroundColor: "#EF233C",
        padding: 14,
        borderRadius: 8,
        alignItems: "center",
        marginTop: 10,
        width: "100%",
    },
    buttonText: {
        color: "#FFF",
        fontSize: 16,
        fontWeight: "bold",
    },
    loginButton: {
        marginTop: 20,
    },
    loginText: {
        color: "#8D99AE",
    },
});

export default RegisterView;
