import axios from 'axios';

const API = "http://localhost:3000" ; /* coloca el " /" */

export const inicioSesion = async (datos) => {
  console.log("entro a inicios de sesion" )
  console.log({datos})
  const datos2 = {
    email: 'ce',
    password: '1212',
  };
  const apiUrl= `http://localhost:3000/api/movil/inicioSesion`;
  const response =  await axios.post(apiUrl , datos)
  console.log(response)
  console.log("se logeo correctamente :"  + response.data._id)
  return await response.json();
};

export const getTasks = async () => {
  const res = await fetch(API, {
    method: "GET",
  });

  return await res.json();
};

export const saveTask = async (newTask) => {
  const res = await fetch(API, {
    method: "POST",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
    },
    body: JSON.stringify(newTask),
  });
  return await res.json();
};

export const getTask = async (taskId) => {
  const res = await fetch(`${API}/${taskId}`);
  return await res.json();
};

export const updateTask = async (taskId, newTask) => {
  console.log(taskId, newTask)
  const res = await fetch(`${API}/${taskId}`, {
    method: "PUT",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
    },
    body: JSON.stringify(newTask),
  });
  return res;
};
