import React from 'react';
import { View, Text, FlatList, TouchableOpacity } from 'react-native';

const ListHorarios = ({ route  , navigation}) => {
  const { datos } = route.params || { datos: [] };

  const dataItems = datos.map((item, index) => ({
    key: String(index), // Usamos el índice como clave ya que no tenemos identificadores únicos
    label: `Elemento ${index + 1}`,
  }));

  const renderItem = ({ item }) => (
    <View style={{ marginBottom: 10, padding: 10, borderWidth: 1, borderColor: 'gray' }}>
      <Text>{item.label}</Text>
      <TouchableOpacity onPress={() => handleVerMas(item)}>
        <Text style={{ color: 'blue' }}>Ver más</Text>
      </TouchableOpacity>
    </View>
  );

  const handleVerMas = (item) => {
    // Implementa la lógica para manejar el botón "Ver más" aquí
    console.log('Ver más', item);
  };

  return (
    <View>
      <Text>Estos son los datos recibidos:</Text>
      <FlatList
        data={dataItems}
        renderItem={renderItem}
        keyExtractor={item => item.key}
      />
    </View>
  );
};

export default ListHorarios;
