import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Image,
  StyleSheet,
} from "react-native";
import { useNavigation } from "@react-navigation/native";
import userService from '../services/userService'; // Asegúrate de importar authService o el servicio de autenticación correcto

export default function LoginView() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigation = useNavigation(); // Asignar useNavigation a la variable 'navigation'

  const handleLogin = async () => {
    try {
      const token = await userService.login(username, password);
      // Verificar si se obtuvo un token
      if (token) {
        // Lógica para guardar el token en el cliente (ejemplo con AsyncStorage)
        // AsyncStorage.setItem('token', token);

        // Redirigir a la siguiente pantalla después del inicio de sesión exitoso
        navigation.navigate('Scanned');
      } else {
        console.error('No se pudo obtener el token después del inicio de sesión');
        // Mostrar un mensaje al usuario indicando un problema con el inicio de sesión
      }
    } catch (error) {
      console.error('Error al iniciar sesión:', error); // Modificación aquí para mostrar detalles completos del error
      // Lógica para manejar el error, por ejemplo, mostrar un mensaje al usuario
    }
  };



  const handleNavigateToScanned = () => {
    navigation.navigate("Scanned");
  };

  const handleNavigateToRegister = () => {
    navigation.navigate('Register'); // Asegúrate de que 'Register' sea la correcta
  };

  return (
    <View style={styles.container}>
      {/* Logo */}
      <Image
        source={{
          uri:
            "https://thumbs.dreamstime.com/b/hand-pointing-qr-code-vector-blue-concept-icon-symbol-hand-pointing-qr-code-vector-blue-concept-icon-symbol-thin-line-109008386.jpg",
        }}
        style={styles.logo}
      />

      {/* Resto del contenido */}
      <Text style={styles.title}>Iniciar Sesión</Text>
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Correo electrónico"
          onChangeText={setUsername}
          value={username}
        />
      </View>
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Contraseña"
          onChangeText={setPassword}
          value={password}
          secureTextEntry
        />
      </View>
      <TouchableOpacity style={styles.button} onPress={handleLogin}>
        <Text style={styles.buttonText}>Iniciar Sesión</Text>
      </TouchableOpacity>
      <TouchableOpacity
      style={styles.registerButton}
      onPress={handleNavigateToRegister}
    >
      <Text style={styles.registerText}>¿Aún no tienes cuenta? Regístrate</Text>
    </TouchableOpacity>
    </View>
  );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    padding: 16,
    backgroundColor: "#2B2D42",
  },
  logo: {
    width: 150,
    height: 150,
    marginBottom: 20,
    resizeMode: "contain", // Ajustar al contenido del tamaño definido
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    color: "#8D99AE",
    marginBottom: 16,
  },
  inputContainer: {
    marginBottom: 16,
    width: "100%",
  },
  input: {
    backgroundColor: "#EDF2F4",
    padding: 10,
    borderRadius: 8,
    fontSize: 16,
  },
  button: {
    backgroundColor: "#EF233C",
    padding: 14,
    borderRadius: 8,
    alignItems: "center",
    marginTop: 10,
    width: "100%",
  },
  buttonText: {
    color: "#FFF",
    fontSize: 16,
    fontWeight: "bold",
  },
});
