import React, { useState, useEffect } from 'react';
import { Text, View, StyleSheet, Button, Animated, Easing } from 'react-native';
import { BarCodeScanner } from 'expo-barcode-scanner';

export default function ScannedView() {
  const [hasPermission, setHasPermission] = useState(null);
  const [scanned, setScanned] = useState(false);
  const [scannedText, setScannedText] = useState('Aún no se ha escaneado nada');
  const fadeIn = new Animated.Value(0);

  const askForCameraPermission = async () => {
    const { status } = await BarCodeScanner.requestPermissionsAsync();
    setHasPermission(status === 'granted');
  };

  useEffect(() => {
    askForCameraPermission();
    fadeInText();
  }, []);

  const fadeInText = () => {
    Animated.timing(fadeIn, {
      toValue: 1,
      duration: 1500,
      easing: Easing.ease,
      useNativeDriver: true,
    }).start();
  };

  const handleBarCodeScanned = ({ type, data }) => {
    setScanned(true);
    setScannedText(`Type: ${type}\nData: ${data}`);
    fadeInText();
    console.log(`Type: ${type}\nData: ${data}`);
  };

  if (hasPermission === null) {
    return (
      <View style={styles.container}>
        <Text>Solicitando permiso para acceder a la cámara...</Text>
      </View>
    );
  }

  if (hasPermission === false) {
    return (
      <View style={styles.container}>
        <Text style={styles.text}>Sin acceso a la cámara</Text>
        <Button title="Permitir acceso a la cámara" onPress={askForCameraPermission} />
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.barcodeBox}>
        <BarCodeScanner
          onBarCodeScanned={scanned ? undefined : handleBarCodeScanned}
          style={styles.scanner}
        />
      </View>
      <Animated.Text style={[styles.scanText, { opacity: fadeIn }]}>{scannedText}</Animated.Text>
      {scanned && (
        <Button title="Escanear de nuevo" onPress={() => setScanned(false)} color="tomato" />
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  text: {
    margin: 10,
  },
  barcodeBox: {
    alignItems: 'center',
    justifyContent: 'center',
    height: 300,
    width: 300,
    borderRadius: 30,
    overflow: 'hidden',
    backgroundColor: 'tomato',
    marginBottom: 20,
  },
  scanner: {
    height: '100%',
    width: '100%',
  },
  scanText: {
    fontSize: 16,
    margin: 20,
    textAlign: 'center',
    color: 'white',
  },
});
