import React from 'react';
import { View, Text, TextInput, TouchableOpacity, Image, StyleSheet } from 'react-native';
import { useNavigation } from '@react-navigation/native';

export function RegisterView() {
  const navigation = useNavigation();

  const handleNavigateToLogin = () => {
    navigation.navigate('LoginView')
  };

  const handleRegister = () => {
    // Lógica para el registro de usuario
    // Puedes realizar llamadas a API, validar datos, etc.

    // Después de completar el registro, puedes redirigir al usuario a la vista de inicio de sesión
    navigation.navigate('LoginView');
  };

  return (
    <View style={styles.container}>
      {/* Logo */}
      <Image
        source={{
          uri:
            'https://thumbs.dreamstime.com/b/hand-pointing-qr-code-vector-blue-concept-icon-symbol-hand-pointing-qr-code-vector-blue-concept-icon-symbol-thin-line-109008386.jpg',
        }}
        style={styles.logo}
      />

      {/* Resto del contenido */}
      <Text style={styles.title}>Registrarse</Text>
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Correo electrónico"
          autoCapitalize="none"
        />
      </View>
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Contraseña"
          secureTextEntry
        />
      </View>
      <TouchableOpacity style={styles.button} onPress={handleRegister}>
        <Text style={styles.buttonText}>Registrarse</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.loginButton} onPress={handleNavigateToLogin}>
        <Text style={styles.loginText}>¿Ya tienes cuenta? Inicia sesión</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: "center",
      alignItems: "center",
      padding: 16,
      backgroundColor: "#2B2D42",
    },
    logo: {
      width: 150,
      height: 150,
      marginBottom: 20,
      resizeMode: "contain", // Ajustar al contenido del tamaño definido
    },
    title: {
      fontSize: 24,
      fontWeight: "bold",
      color: "#8D99AE",
      marginBottom: 16,
    },
    inputContainer: {
      marginBottom: 16,
      width: "100%",
    },
    input: {
      backgroundColor: "#EDF2F4",
      padding: 10,
      borderRadius: 8,
      fontSize: 16,
    },
    button: {
      backgroundColor: "#EF233C",
      padding: 14,
      borderRadius: 8,
      alignItems: "center",
      marginTop: 10,
      width: "100%",
    },
    buttonText: {
      color: "#FFF",
      fontSize: 16,
      fontWeight: "bold",
    },
  });

  export default LoginView;
