const userService = {
  apiUrl: 'http://localhost:3000/api/u',

  login: async (username, password) => {
    try {
      const response = await fetch(`${userService.apiUrl}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });
      const data = await response.json();
      localStorage.setItem('authToken', data.token);
      // Aquí podrías usar un sistema de notificaciones o mensajes para mostrar que el inicio de sesión fue exitoso
      return data.token;
    } catch (error) {
      throw new Error('Error al iniciar sesión');
    }
  },

  register: async (username, password) => {
    try {
      const response = await fetch(`${userService.apiUrl}/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });
      const data = await response.json();
      localStorage.setItem('authToken', data.token);
      // Aquí podrías usar un sistema de notificaciones o mensajes para mostrar que el registro fue exitoso
      return data;
    } catch (error) {
      throw new Error('Error al registrar');
    }
  },

  logout: () => {
    localStorage.removeItem('authToken');
  },

  isLoggedIn: () => {
    return !!localStorage.getItem('authToken');
  },
};

export default userService;
